from pybricks.parameters import Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
left_wheel = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right_wheel = Motor(Port.B, Direction.CLOCKWISE)
drive_base = DriveBase(left_wheel, right_wheel, 105, 155)


# The main program starts here.
print('Hello, Davaid!')
print('writing letter D')
drive_base.straight(500)
drive_base.turn(90)
drive_base.arc(250, angle=180)
drive_base.turn(170)
drive_base.straight(500)
print('writing letter A')
drive_base.turn(-60)
drive_base.straight(550)
drive_base.turn(120)
drive_base.straight(225)
drive_base.turn(100)
drive_base.straight(100)
drive_base.turn(180)
drive_base.straight(100)
drive_base.turn(60)
drive_base.straight(225)
print('return to base')
drive_base.turn(120)
drive_base.straight(1000)
drive_base.turn(90)
